import { StubContext } from "../core/StubContext";
import { StubContractConfig } from "../types";

export function StubContract (config?: StubContractConfig): ClassDecorator {

    const props = config ?? {
        
    };

    return (constructor: Function) => {

        const ctx = StubContext.getContext(props.contextDomain);

        return ctx.defineContract(
            props,
            constructor
        );
        
    }
}