import { ProxyContext, StubContext } from '../src';
import { LoopbackServer, LoopbackClient } from './LoopbackChannel';

test('LoopbackChannel', async () => {

    const server = StubContext.getChannel<LoopbackServer>(LoopbackServer);
    const client = ProxyContext.getChannel<LoopbackClient>(LoopbackClient);

    const result = await client.foo();

    expect(result)
        .toBe("Hello from foo");

});